import{_ as a,c as e,o as l,ae as t}from"./chunks/framework.CDjunVez.js";const m=JSON.parse('{"title":"无监督学习","description":"","frontmatter":{},"headers":[],"relativePath":"ai/neural/ml/unsupervised.md","filePath":"ai/neural/ml/unsupervised.md"}'),n={name:"ai/neural/ml/unsupervised.md"};function r(o,i,s,u,d,c){return l(),e("div",null,[...i[0]||(i[0]=[t('<h1 id="无监督学习" tabindex="-1">无监督学习 <a class="header-anchor" href="#无监督学习" aria-label="Permalink to &quot;无监督学习&quot;">​</a></h1><h2 id="无监督学习-1" tabindex="-1">无监督学习 <a class="header-anchor" href="#无监督学习-1" aria-label="Permalink to &quot;无监督学习&quot;">​</a></h2><p>无标签，发现数据内在结构。</p><h3 id="聚类-k-means" tabindex="-1">聚类：K-Means <a class="header-anchor" href="#聚类-k-means" aria-label="Permalink to &quot;聚类：K-Means&quot;">​</a></h3><p>将数据分K群，使群内相似、群间差异大。</p><ul><li><strong>步骤</strong>： <ol><li>随机初始化K个中心。</li><li>分配：每个点到最近中心。</li><li>更新：中心=簇均值。</li><li>收敛或迭代固定次数。</li></ol></li><li><strong>距离</strong>：欧氏。</li><li><strong>优点</strong>：简单、快、可扩展。</li><li><strong>缺点</strong>：需预设K（肘部法/轮廓系数选）、对初始化敏感（K-Means++改善）、非球形簇差。</li><li><strong>评估</strong>：轮廓系数、Calinski-Harabasz。</li></ul><h3 id="降维-dimensionality-reduction" tabindex="-1">降维（Dimensionality Reduction） <a class="header-anchor" href="#降维-dimensionality-reduction" aria-label="Permalink to &quot;降维（Dimensionality Reduction）&quot;">​</a></h3><p>压缩高维特征，保留主要信息，缓解维度灾难。</p><ul><li><strong>PCA（主成分分析）</strong>： <ul><li>目标：最大方差投影。</li><li>步骤：中心化 → 协方差矩阵 → 特征向量 → 选前K维。</li><li>线性、无监督。</li></ul></li><li><strong>t-SNE</strong>：非线性，适合可视化（低维嵌入）。</li><li><strong>优点</strong>：降噪、加速训练、可视化。</li><li><strong>缺点</strong>：PCA信息丢失、t-SNE不适合新数据。</li></ul>',9)])])}const p=a(n,[["render",r]]);export{m as __pageData,p as default};
