import{_ as e,o as r,c as i,ah as t}from"./chunks/framework.DYrKkesV.js";const h=JSON.parse('{"title":"综合范式","description":"","frontmatter":{"title":"综合范式","order":999},"headers":[],"relativePath":"design/summary/index.md","filePath":"design/summary/index.md"}'),l={name:"design/summary/index.md"};function n(s,a,o,d,p,_){return r(),i("div",null,[...a[0]||(a[0]=[t('<h1 id="综合范式" tabindex="-1">综合范式 <a class="header-anchor" href="#综合范式" aria-label="Permalink to “综合范式”">​</a></h1><p>综合范式是为了破除经典理论的隔阂，从一个更高更全面的维度去理解这些理论的本质，总结概括杂乱的理论商品，减少选择困难，并走到实践中去，提出具体的实践方案，让理论变得可行。</p><h2 id="时空观思维" tabindex="-1">时空观思维 <a class="header-anchor" href="#时空观思维" aria-label="Permalink to “时空观思维”">​</a></h2><p>分析设计一个程序系统，可以从<strong>空间结构</strong>和<strong>时间流程</strong>两个大方向入手，先看空间结构，然后看结构之间的交互流程。</p><h3 id="结构" tabindex="-1">结构 <a class="header-anchor" href="#结构" aria-label="Permalink to “结构”">​</a></h3><p>当下的软件系统在空间形态上有两个明显的边界，一个是进程边界，一个是主机边界，使得空间被划分成 3 层结构。</p><p><strong>单进程</strong></p><ul><li>模块拆分</li><li>依赖管理</li><li>状态管理</li></ul><p><strong>进程间/本地主机</strong></p><ul><li>文件管理</li><li>微服务治理</li><li>数据与配置分离</li></ul><p><strong>跨主机</strong></p><ul><li>分布式锁</li><li>通信代理</li></ul><p>作用域和生命周期</p><p>空间有三个维度，但是我们的软件设计的结构是基于二维的。</p>',14)])])}const m=e(l,[["render",n]]);export{h as __pageData,m as default};
